import React from 'react';
import {Field, Form} from 'react-final-form';
import arrayMutators from 'final-form-arrays'
import {FieldArray} from 'react-final-form-arrays'

const onSubmit = async values => {
    console.log(values);
};

const TeacherTimetable = () => {
    return (
        <Form
            onSubmit={onSubmit}
            mutators={{
                ...arrayMutators
            }}
            render={({
                         handleSubmit,
                         form: {
                             mutators: {push, pop}
                         },
                         pristine,
                         form,
                         submitting,
                         values
                     }) => {
                return (
                    <form onSubmit={handleSubmit}>
                        <FieldArray name="customers">
                            {({fields}) => (
                                <div>
                                    {fields.length === 0 && <div>
                                        <label>First Name</label>
                                        <Field name={`${name}.firstName`} component="input"/>
                                    </div>}
                                    {fields.map((name, index) => (
                                        <div key={name}>
                                            <div>
                                                <label>First Name</label>
                                                <Field name={`${name}.firstName`} component="input"/>
                                            </div>
                                            {fields.length !== 1 &&  <button type="button" onClick={() => fields.remove(index)}>
                                                Remove
                                            </button>}
                                        </div>
                                    ))}
                                    <button
                                        type="button"
                                        onClick={() => fields.push({firstName: ''})}
                                    >
                                        Add
                                    </button>
                                </div>
                            )}
                        </FieldArray>
                    </form>
                    /*<form onSubmit={handleSubmit}>
                        <FieldArray name="lessonsTime">
                            {({fields}) =>
                                    fields.map((name, index) => (
                                        <div key={name}>
                                            <label>Введите время {index + 1} занятия</label>
                                            <Field
                                                name={`${name}`}
                                                component="input"
                                                placeholder="00:00-00:00"
                                            /> {fields.length !== 1 &&  <span
                                            onClick={() => fields.remove(index)}
                                            style={{cursor: 'pointer'}}
                                        >❌</span>}
                                        </div>
                                    ))
                            }
                        </FieldArray>
                        <div className="buttons">
                            <button
                                type="button"
                                onClick={() => push('lessonsTime', undefined)}
                            >
                                Добавить пару
                            </button>
                        </div>
                        <div className="buttons">
                            <button type="submit" disabled={submitting || pristine}>
                                Создать расписание
                            </button>
                        </div>
                    </form>*/
                )
            }}
        />
    );
};

export default TeacherTimetable;
